class Bateau:
    # Constructeur
    def __init__(self, couleur, type_energie) -> None:
        self.__couleur = couleur
        self.__type_energie = type_energie
    # Ascesseurs et mutateurs
    @property
    def couleur (self):
        return self.__couleur
    @couleur.setter

class Voiture:
    # Constructeur
    def __init__(self, couleur, nb_roues) -> None:
        self.__couleur = couleur
        self.__nb_roues = nb_roues
    # Ascesseurs et mutateurs
    @property
    def couleur (self):
        return self.__couleur
    @couleur.setter

class VoitureAmphibie (Bateau, Voiture):
    def __init__(self, couleur, type_energie, nb_roues) -> None:
        # super().__init__(couleur, type_energie)
        # super().__init__(couleur, nb_roues)
        Bateau.__init__(self, couleur, type_energie)
        Voiture.__init__(self, couleur, nb_roues)
        self.__mode = 0 # O = sol ; 1 = eau
    @property
    def mode_sol(self):
        return self.__mode == 0

class Classroom:
    def __init__(self, main_teacher, nb_places_max):
        self.students = []
        self.MAX_NB_PLACE = nb_places_max
        self.main_teacher = main_teacher
    # Méthodes
    def add_student(self, student):
        if isinstance(student, Student):
            if len(self.students) < self.MAX_NB_PLACE:
                self.students.append(student)

class Student:
    def __init__(self, lastname, firstname):
        self.lastname = lastname
        self.firstname = firstname
        self.matricule = lastname[:4] + firstname[:3]
    def skip_class(self, course):
        print(f"{self.lastname} {self.firstname} sèche le cours de {course}.")
    def go_to_class(self, course):
        print(f"{self.lastname} {self.firstname} suit le cours de {course}.")

